sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var incidentState = JSON.parse(g_scratchpad.incidentState);
    var mandatory = false;
    if (g_form.hasField('incident_state')) {
        var incident_state = g_form.getValue('incident_state');
        mandatory = incident_state == 6|| incident_state == 7;
    }
    if (g_form.hasField('state')) {
        var state = g_form.getValue('state');
        mandatory = state == 6 || state == 7;
    }
    g_form.setSectionDisplay('resolution_information', mandatory);
}
