sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) return; // Skip if form is loading

    var caller = g_form.getReference('caller_id'); // Get the selected caller's user record

    if (caller) {
        // Check if the caller is VIP
        var isVIP = caller.vip; // Assuming 'vip' is the field on the sys_user table

        // If VIP, set the flag icon or text in the VIP flag field
        if (isVIP) {
            g_form.setValue('caller_id', '<img src="sn_tile_icon/sn-promin-process-findings-connected.svg">');
        } else {
            g_form.setValue('vip_flag', ''); // Clear the flag if not VIP
        }
    }
}
