sys_script_include.script:
var CIUtils1 = Class.create();
CIUtils1.prototype = {
    initialize: function() {
    },
	canShowRefreshServicesUIAction: function(tableClassName) {
        var ciTable = gs.getProperty('com.snc.task.refresh_impacted_services');
        if (ciTable) {
            var tableList = ciTable.split(',');
            for (var i = 0; i < tableList.length; ++i) {
                var table = tableList[i];
                table = table.trim();
                if (tableClassName + "" === table)
                    return true;
            }
        } else
            return false;
    },
		hasReopened: function() {
		var incidentGr = this._getGr();
		return (incidentGr.incident_state.changesFrom(6) || 
				incidentGr.incident_state.changesFrom(7) || 
				incidentGr.incident_state.changesFrom(8)) && 
			incidentGr.incident_state != 6 && 
			incidentGr.incident_state !=7 && 
			incidentGr.incident_state != 8;
	},

    type: 'CIUtils1'
};
